package br.com.elvisassis.dslist.controller;import br.com.elvisassis.dslist.dto.GameMinDTO;import br.com.elvisassis.dslist.services.GameService;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/games")public class GameController {    private GameService service;    public GameController(GameService service) {        this.service = service;    }    @GetMapping    public ResponseEntity<List<GameMinDTO>> findAll( ){        var response = service.findAll();        return ResponseEntity.ok(response);    }    @GetMapping(            value = "/{id}",            produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<GameMinDTO> findGameMinBy(@PathVariable Long id) {        var response = service.findGameMinBy(id);        return ResponseEntity.ok(response);    }}