package br.com.elvisassis.dslist.controller;import br.com.elvisassis.dslist.dto.GameListDTO;import br.com.elvisassis.dslist.services.GameListService;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/lists")public class GameListController {    private GameListService service;    public GameListController(GameListService service) {        this.service = service;    }    @GetMapping    public ResponseEntity<List<GameListDTO>> findAll() {        var response = service.findAll();        return ResponseEntity.ok(response);    }}