package br.com.elvisassis.dslist.controllers;import br.com.elvisassis.dslist.dto.GameListDTO;import br.com.elvisassis.dslist.dto.GameMinDTO;import br.com.elvisassis.dslist.dto.ReplacementDTO;import br.com.elvisassis.dslist.services.GameListService;import br.com.elvisassis.dslist.services.GameService;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/lists")public class GameListController {    private GameListService service;    private GameService gameService;    public GameListController(GameListService service, GameService gameService) {        this.service = service;        this.gameService = gameService;    }    @GetMapping    public ResponseEntity<List<GameListDTO>> findAll() {        var response = service.findAll();        return ResponseEntity.ok(response);    }    @GetMapping(value = "/{listId}/games",                produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<List<GameMinDTO>> findByList(@PathVariable Long listId) {        var response = gameService.searchByList(listId);        return ResponseEntity.ok(response);    }    @PostMapping(value = "/{listId}/replecement",            consumes = MediaType.APPLICATION_JSON_VALUE,            produces = MediaType.APPLICATION_JSON_VALUE)    public void move(@PathVariable Long listId, @RequestBody ReplacementDTO body) {        service.move(listId, body.getSourceIndex(), body.getDestinationIndex());    }}