package br.com.elvisassis.dslist.dto;import br.com.elvisassis.dslist.entities.Game;import br.com.elvisassis.dslist.mapprer.GameMapper;import org.springframework.beans.BeanUtils;import java.util.Objects;public class GameDTO {  private Long id;  private String title;  private Integer year;  private String imgUrl;  private String shortDescription;  private String genre;  private String platforms;  private String longDescription;  public GameDTO() {  }  public GameDTO(Game entity) {    BeanUtils.copyProperties(entity, this);  }  public Game toEntity(GameDTO game) {    return GameMapper.INSTANCE.toEntityFull(this);  }  public Long getId() {    return id;  }  public void setId(Long id) {    this.id = id;  }  public String getTitle() {    return title;  }  public void setTitle(String title) {    this.title = title;  }  public Integer getYear() {    return year;  }  public void setYear(Integer year) {    this.year = year;  }  public String getImgUrl() {    return imgUrl;  }  public void setImgUrl(String imgUrl) {    this.imgUrl = imgUrl;  }  public String getShortDescription() {    return shortDescription;  }  public void setShortDescription(String shortDescription) {    this.shortDescription = shortDescription;  }  public String getGenre() {    return genre;  }  public void setGenre(String genre) {    this.genre = genre;  }  public String getPlatforms() {    return platforms;  }  public void setPlatforms(String platforms) {    this.platforms = platforms;  }  public String getLongDescription() {    return longDescription;  }  public void setLongDescription(String longDescription) {    this.longDescription = longDescription;  }  @Override  public boolean equals(Object o) {    if (!(o instanceof GameDTO that)) return false;      return Objects.equals(getId(), that.getId());  }  @Override  public int hashCode() {    return Objects.hashCode(getId());  }}