package br.com.elvisassis.dslist.execptions.handle;import br.com.elvisassis.dslist.execptions.ExceptionResponse;import br.com.elvisassis.dslist.execptions.RecordNotFoundException;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.context.request.WebRequest;import org.springframework.web.servlet.mvc.method.annotation.ResponseEntityExceptionHandler;import java.util.Date;@ControllerAdvice@RestControllerpublic class CustomEntityResponseHandle extends ResponseEntityExceptionHandler {  @ExceptionHandler(RecordNotFoundException.class)  public final ResponseEntity<ExceptionResponse> handleRecordNotFoundExceptionn(Exception ex, WebRequest request) {    var response = new ExceptionResponse(new Date(), ex.getMessage() , request.getDescription(false));    return new ResponseEntity<>(response, HttpStatus.NOT_FOUND);  }}