package br.com.elvisassis.dslist.services;import br.com.elvisassis.dslist.dto.GameListDTO;import br.com.elvisassis.dslist.entities.GameList;import br.com.elvisassis.dslist.repositories.GameListRepository;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Servicepublic class GameListService {  private final GameListRepository repository;  public GameListService(GameListRepository repository) {    this.repository = repository;  }  @Transactional(readOnly = true)  public List<GameListDTO> findAll() {    var result = repository.findAll();    return result.stream()            .map(gameList -> gameList.toDto(gameList)).toList();  }}