package br.com.elvisassis.dslist.services;import br.com.elvisassis.dslist.dto.GameMinDTO;import br.com.elvisassis.dslist.repositories.GameRepository;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class GameService {    private GameRepository repository;    public GameService(GameRepository repository) {        this.repository = repository;    }    public List<GameMinDTO> findAll() {        var result = repository.findAll();        return result.stream()                .map(game -> game.toDTO(game))                .toList();    }    public GameMinDTO findGameMinBy(Long id) {        var result = repository.findById(id).orElseThrow();        return result.toDTO(result);    }}